import 'package:flutter/material.dart';
import 'login_screen.dart'; // Importa a tela de login

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    // MaterialApp para definir o tema do aplicativo
    return MaterialApp(
      home: HomeScreen(), // Define a tela inicial
    );
  }
}

class HomeScreen extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: Text('Notas Alunos')), // Título do app
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: <Widget>[
            // Logotipo da escola (simulado)
            Image.network('data:image/png;base64,', height: 150),
            SizedBox(height: 20), // Espaçamento
            ElevatedButton(
              // Botão que leva à tela de login
              onPressed: () {
                Navigator.push(
                  context,
                  MaterialPageRoute(builder: (context) => LoginScreen()),
                );
              },
              child: Text('Ir para Login'), // Texto do botão
            ),
          ],
        ),
      ),
    );
  }
}